package sampleExercises;

public class PrimeNumberTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 7;
		if((num != 2) && (num%2 == 0)) {
			System.out.println("Number is not prime:" +num);
		}else {
			System.out.println("Number is prime:" +num);
		}

	}

}
